import{_ as e,c as o,o as l,d}from"./app.15e8eba7.js";const _=JSON.parse('{"title":"\u7B80\u8FF0 Raft \u534F\u8BAE","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u573A\u666F","slug":"\u573A\u666F"},{"level":3,"title":"\u5355\u673A\u6A21\u5F0F","slug":"\u5355\u673A\u6A21\u5F0F"},{"level":3,"title":"\u5206\u5E03\u5F0F","slug":"\u5206\u5E03\u5F0F"},{"level":2,"title":"\u9009\u4E3E\u673A\u5236","slug":"\u9009\u4E3E\u673A\u5236"},{"level":3,"title":"\u6848\u4F8B","slug":"\u6848\u4F8B"},{"level":2,"title":"\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u8282\u70B9\u6570","slug":"\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u8282\u70B9\u6570"},{"level":2,"title":"\u6570\u636E\u590D\u5236\u8FC7\u7A0B(\u521D\u7EA7)","slug":"\u6570\u636E\u590D\u5236\u8FC7\u7A0B-\u521D\u7EA7"},{"level":3,"title":"\u57FA\u672C\u8FC7\u7A0B","slug":"\u57FA\u672C\u8FC7\u7A0B"}],"relativePath":"go/interview/go-raft.md","lastUpdated":null}'),a={name:"go/interview/go-raft.md"},t=d('<h1 id="\u7B80\u8FF0-raft-\u534F\u8BAE" tabindex="-1">\u7B80\u8FF0 Raft \u534F\u8BAE <a class="header-anchor" href="#\u7B80\u8FF0-raft-\u534F\u8BAE" aria-hidden="true">#</a></h1><h2 id="\u573A\u666F" tabindex="-1">\u573A\u666F <a class="header-anchor" href="#\u573A\u666F" aria-hidden="true">#</a></h2><h3 id="\u5355\u673A\u6A21\u5F0F" tabindex="-1">\u5355\u673A\u6A21\u5F0F <a class="header-anchor" href="#\u5355\u673A\u6A21\u5F0F" aria-hidden="true">#</a></h3><blockquote><p>\u4E00\u711A\u4FF1\u711A\uFF0C\u5982\u679C\u8FD9\u4E2A\u8282\u70B9\u53D1\u751F\u5B95\u673A\uFF0C\u90A3\u4E48\u5B83\u7684\u6570\u636E\u5C31\u4F1A\u4E22\u5931\uFF0C\u800C\u4E14\u65E0\u6CD5\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\uFF0C\u5E76\u4E14\u6570\u636E\u65E0\u6CD5\u6062\u590D\u3002</p></blockquote><h3 id="\u5206\u5E03\u5F0F" tabindex="-1">\u5206\u5E03\u5F0F <a class="header-anchor" href="#\u5206\u5E03\u5F0F" aria-hidden="true">#</a></h3><p>\u5206\u5E03\u5F0F\u96C6\u7FA4\uFF1A</p><blockquote><p>\u4E00\u65E6\u5355\u8282\u70B9\u5B95\u673A\uFF0C\u5176\u4ED6\u8282\u70B9\u4F9D\u7136\u80FD\u63D0\u4F9B\u670D\u52A1</p></blockquote><p>\u5173\u952E\u7684\u662F\uFF1A\u6570\u636E\u5E76\u4E0D\u4F1A\u4E22\u5931\uFF0C\u5176\u4E2D\u4E00\u4E2A\u91CD\u8981\u8FC7\u7A0B\u5C31\u662F<strong>\u6570\u636E\u4E00\u81F4\u6027</strong></p><p>\u8981\u5B9E\u73B0\u4E00\u81F4\u6027\uFF1A\u4E00\u822C\u6765\u8BB2\u8282\u70B9\u4E2D\u9700\u8981\u6709\u4E2A\u4E3B\u8282\u70B9\u6765\u5BF9\u6570\u636E\u65E5\u5FD7\u8FDB\u884C\u7EDF\u4E00\u7BA1\u7406\u3010\u590D\u5236\u3011</p><h2 id="\u9009\u4E3E\u673A\u5236" tabindex="-1">\u9009\u4E3E\u673A\u5236 <a class="header-anchor" href="#\u9009\u4E3E\u673A\u5236" aria-hidden="true">#</a></h2><h3 id="\u6848\u4F8B" tabindex="-1">\u6848\u4F8B <a class="header-anchor" href="#\u6848\u4F8B" aria-hidden="true">#</a></h3><p>\u6BD4\u5982\u90E8\u7F72 3 \u4E2A etcd</p><p>\u5047\u8BBE\u6709\u5982\u4E0B\u89D2\u8272\uFF1A</p><ul><li>\u9886\u5BFC\u8005\uFF1Aleader \u4E3B\u8282\u70B9</li><li>\u8DDF\u968F\u8005\uFF1Afollower</li><li>\u5019\u9009\u8005\uFF1Acandidate</li></ul><blockquote><p>\u4E09\u4E2A\u8282\u70B9\uFF0C\u521D\u59CB\u72B6\u6001\u90FD\u662F<code>follower</code>\uFF0C\u90FD\u6709\u4E2A<code>term</code>\u503C\u90FD\u662F 0\uFF0C<code>term</code>\u4EE3\u8868\u4E86\u4EFB\u671F</p><p>\u5E76\u4E14\u968F\u673A\u5206\u914D\u4E00\u4E2A\u968F\u673A\u65F6\u95F4\u7ED9 3 \u4E2A\u8282\u70B9\u3002</p></blockquote><hr><div class="tip custom-block"><p class="custom-block-title">\u6B63\u5E38\u60C5\u51B5\u4E0B</p><p>\u5047\u8BBE\u73B0\u5728\u6709 A B C \u4E09\u4E2A\u8282\u70B9\uFF0C\u968F\u673A\u5206\u914D\u7ED9\u8FD9\u4E09\u4E2A\u8282\u70B9\u4E00\u4E2A\u968F\u673A\u65F6\u95F4\uFF0C\u54EA\u4E00\u4E2A\u6700\u77ED\uFF0C\u54EA\u4E00\u4E2A\u5148\u53D1\u8D77\u201C\u9009\u4E3E\u6295\u7968\u201D\uFF1B</p><p>\u5047\u8BBE\u201CA\u201D\u5148\u82CF\u9192\uFF0C\u5B83\u8981\u5E72 3 \u4EF6\u4E8B\uFF1A</p><ol><li>A \u8BBE\u7F6E<code>term = 1</code>\uFF0C\u5E76\u628A\u81EA\u5DF1\u89D2\u8272\u6539\u4E3A<code>candidate</code></li><li>\u7ED9\u81EA\u5DF1\u6295\u4E00\u7968</li><li>\u7ED9\u5176\u4ED6 2 \u4E2A\u8282\u70B9\u53D1\u751F<code>Msg(\u5E26term\u503C)</code>\uFF0C\u7B49\u5F85\u56DE\u590D\u3010\u5C31\u662F\u7B49\u5F85 B \u548C C \u662F\u5426\u5BF9\u81EA\u5DF1\u6295\u7968\u3011</li></ol><hr><p>B \u8282\u70B9\u6536\u5230 A \u53D1\u9001\u6765\u7684\u6295\u7968<code>Msg</code>\uFF0C\u5E76\u83B7\u53D6<code>term</code>\u503C\uFF0C\u53D1\u73B0\u81EA\u5DF1\u7684\u6BD4 A \u5C0F\uFF0C\u5219</p><ol><li>\u7EE7\u7EED\u4FDD\u6301<code>follower</code>\u89D2\u8272</li><li>\u628A\u81EA\u5DF1\u7684<code>term</code>\u4E5F\u6539\u6210 1</li><li>\u5411 A \u8282\u70B9\u56DE\u6267<code>Msg</code></li></ol><hr><p>\u4F9D\u6B21\u7C7B\u63A8\uFF0CC \u4E5F\u4F1A\u5E72\u4E0A\u9762\u7684\u4E8B\u60C5\uFF0C\u6700\u7EC8 A \u4F1A\u8D62\u5F97\u9009\u4E3E\u3010\u5927\u591A\u6570\u9009\u7968\u3011</p><p>\u4E8E\u662F\uFF0C\u5B83\u4F1A\u7ACB\u523B\u7ED9\u6240\u6709\u8282\u70B9\u53D1\u751F\u5FC3\u8DF3\u6D88\u606F\uFF0C\u907F\u514D\u5176\u4F59\u8282\u70B9\u89E6\u53D1\u65B0\u7684\u9009\u4E3E\u3002</p></div><div class="warning custom-block"><p class="custom-block-title">\u4E0D\u6B63\u5E38\u7684\u60C5\u51B5</p><p>\u7531\u4E8E\u7F51\u7EDC\u7B49\u539F\u56E0\uFF0CA \u548C B \u4E5F\u8BB8\u4F1A\u540C\u65F6\u53D1\u8D77\u9009\u4E3E</p><p><strong>\u6CE8\u610F\uFF1A\u5728\u540C\u4E00\u4EFB\u671F\u5185\uFF0CC \u53EA\u80FD\u7ED9 A \u6216\u8005 B \u6295\u4E00\u7968\uFF0C\u5148\u6765\u5148\u5F97</strong></p><blockquote><p>\u5047\u8BBE A \u5148\u5F97\u5230\uFF0C\u6B64\u65F6\u8FD8\u662F A \u80DC\u51FA</p></blockquote><p>\u8FD8\u662F\u90A3\u6837\uFF0CA \u80DC\u51FA\u4E4B\u540E\u4F1A\u53D1\u51FA\u5FC3\u8DF3\u6D88\u606F\u3002B \u53D1\u73B0 A \u7684<code>term</code>\u6BD4\u4ED6\u5927\uFF0C\u5219\u4F1A\u81EA\u89C9\u7684\u6539\u6210<code>follower</code>\uFF0C\u5E76\u66F4\u65B0<code>term</code>\uFF0C\u53CD\u4E4B\u4EA6\u7136\u3002</p></div><h2 id="\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u8282\u70B9\u6570" tabindex="-1">\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u8282\u70B9\u6570 <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u8282\u70B9\u6570" aria-hidden="true">#</a></h2><p>\u4E0A\u9762\u7684\u6848\u4F8B\u8BF4\u7684\u662F 3 \u4E2A\u8282\u70B9\uFF0C\u4E5F\u5C31\u662F\u5947\u6570\u8282\u70B9\uFF0C\u90A3\u4E48\u6211\u4EEC\u6362\u6210\u5076\u6570\u8282\u70B9\uFF0C\u5C31\u4F1A\u53D1\u73B0\u4F1A\u51FA\u73B0\uFF0C<strong>A \u548C B \u51FA\u73B0\u5E73\u7968\u7684\u72B6\u6001\uFF0C\u5C06\u4E0D\u4F1A\u4EA7\u751F<code>leader</code></strong>\uFF0C\u8FD9\u6837\u96C6\u7FA4\u5C31\u662F\u5E9F\u7684\uFF0C\u4E0D\u53EF\u4EE5\u63D0\u4F9B\u670D\u52A1\u3002</p><div class="danger custom-block"><p class="custom-block-title">\u6CE8\u610F</p><p>\u5982\u679C\u662F\u5076\u6570\u8282\u70B9\uFF0C\u8FD9\u6837\u5BB9\u6613\u53D1\u751F\u5E73\u7968</p><p>\u5C31\u5F97\u7B49\u5F85\u8D85\u65F6\u540E\u7EE7\u7EED\u53D1\u8D77\u6295\u7968</p><p>\u4ECE\u800C\u5EF6\u957F\u4E86\u7CFB\u7EDF\u9009\u4E3E\u7684\u65F6\u95F4</p></div><h2 id="\u6570\u636E\u590D\u5236\u8FC7\u7A0B-\u521D\u7EA7" tabindex="-1">\u6570\u636E\u590D\u5236\u8FC7\u7A0B(\u521D\u7EA7) <a class="header-anchor" href="#\u6570\u636E\u590D\u5236\u8FC7\u7A0B-\u521D\u7EA7" aria-hidden="true">#</a></h2><blockquote><p>\u6839\u636E\u4E0A\u9762\u7684\u6848\u4F8B\uFF0CA,B,C \u4E09\u4E2A\u8282\u70B9\u4E00\u822C\u5982\u679C\u60F3\u8981\u5B9E\u73B0\u6570\u636E\u590D\u5236\uFF0C\u4F7F\u7528\u65E5\u5FD7\u7684\u65B9\u5F0F\uFF0C\u6BD4\u5982 MySQL \u5C31\u6709\u4E00\u4E2A<code>bin-log</code>\u65E5\u5FD7\uFF0C\u5B58\u50A8\u4F7F\u7528\u8FC7\u7A0B\u4E2D\u7684\u547D\u4EE4\u5230\u672C\u5730\u65E5\u5FD7\uFF0C\u5982\u679C\u673A\u5668\u5B95\u673A\u91CD\u542F\u540E\u53EF\u4EE5\u6839\u636E\u8FD9\u4E9B\u5B58\u50A8\u547D\u4EE4\u7684\u65E5\u5FD7\u6062\u590D\u6570\u636E\u3002</p></blockquote><h3 id="\u57FA\u672C\u8FC7\u7A0B" tabindex="-1">\u57FA\u672C\u8FC7\u7A0B <a class="header-anchor" href="#\u57FA\u672C\u8FC7\u7A0B" aria-hidden="true">#</a></h3><p>\u6240\u6709\u65E5\u5FD7\u90FD\u5FC5\u987B\u9996\u5148\u63D0\u4EA4\u81F3<code>leader</code>\u8282\u70B9</p><ol><li><code>leader</code>\u52A0\u5165\u672C\u5730\u65E5\u5FD7</li><li><code>leader</code>\u8981\u6C42<code>followers</code>\u540C\u6B65\u65E5\u5FD7</li><li><code>leader</code>\u7B49\u5F85\u591A\u6570\u8282\u70B9\u7684\u53CD\u9988\u3010\u4E0D\u662F\u5168\u90E8\u3011</li><li>\u7B2C\u4E09\u70B9\u6210\u529F\u540E<code>leader</code>\u786E\u8BA4\u64CD\u4F5C OK,\u5E76\u4FEE\u6539\u672C\u5730\u72B6\u6001\u548C\u5B58\u50A8</li><li>\u53D1\u51FA\u5FC3\u8DF3\u8981\u6C42<code>folower</code>\u4E5F\u63D0\u4EA4\u5E76\u5B58\u50A8</li></ol>',26),c=[t];function i(r,s,p,h,n,u){return l(),o("div",null,c)}const b=e(a,[["render",i]]);export{_ as __pageData,b as default};
