import{_ as e,c as o,o as t,d as l}from"./app.f37b2480.js";const h=JSON.parse('{"title":"k8s \u53D1\u5C55\u7ECF\u5386","description":"","frontmatter":{},"headers":[{"level":2,"title":"k8s \u53D1\u5C55\u7ECF\u5386","slug":"k8s-\u53D1\u5C55\u7ECF\u5386"},{"level":3,"title":"\u7279\u70B9\uFF1A","slug":"\u7279\u70B9\uFF1A"},{"level":2,"title":"\u529F\u80FD","slug":"\u529F\u80FD"}],"relativePath":"k8s/introduction.md","lastUpdated":1663602250000}'),i={name:"k8s/introduction.md"},c=l('<blockquote><p>K8s \u662F Google \u5F00\u6E90\u7684\u4E00\u4E2A\u5BB9\u5668\u7F16\u6392\u5F15\u64CE\uFF0C\u5B83\u652F\u6301\u81EA\u52A8\u5316\u90E8\u7F72\u3001\u5927\u89C4\u6A21\u53EF\u4F38\u7F29\u3001\u5E94\u7528\u5BB9\u5668\u5316\u7BA1\u7406\u3002\u5728\u751F\u4EA7\u73AF\u5883\u4E2D\u90E8\u7F72\u4E00\u4E2A\u5E94\u7528\u7A0B\u5E8F\u65F6\uFF0C\u901A\u5E38\u8981\u90E8\u7F72\u8BE5\u5E94\u7528\u7684\u591A\u4E2A\u5B9E\u4F8B\u4EE5\u4FBF\u5BF9\u5E94\u7528\u8BF7\u6C42\u8FDB\u884C\u8D1F\u8F7D\u5747\u8861\u3002</p><p>\u5728 K8S \u4E2D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u521B\u5EFA\u591A\u4E2A\u5BB9\u5668\uFF0C\u6BCF\u4E2A\u5BB9\u5668\u91CC\u9762\u8FD0\u884C\u4E00\u4E2A\u5E94\u7528\u5B9E\u4F8B\uFF0C\u7136\u540E\u901A\u8FC7\u5185\u7F6E\u7684\u8D1F\u8F7D\u5747\u8861\u7B56\u7565\uFF0C\u5B9E\u73B0\u5BF9\u8FD9\u4E00\u7EC4\u5E94\u7528\u5B9E\u4F8B\u7684\u7BA1\u7406\u3001\u53D1\u73B0\u3001\u8BBF\u95EE\uFF0C\u800C\u8FD9\u4E9B\u7EC6\u8282\u90FD\u4E0D\u9700\u8981\u8FD0\u7EF4\u4EBA\u5458\u53BB\u8FDB\u884C\u590D\u6742\u7684\u624B\u5DE5\u914D\u7F6E\u548C\u5904\u7406\u3002</p></blockquote><h2 id="k8s-\u53D1\u5C55\u7ECF\u5386" tabindex="-1">k8s \u53D1\u5C55\u7ECF\u5386 <a class="header-anchor" href="#k8s-\u53D1\u5C55\u7ECF\u5386" aria-hidden="true">#</a></h2><p>K8S \u5B98\u7F51\uFF1A<a href="https://kubernetes.io/zh/docs/tutorials/kubernetes-basics/" target="_blank" rel="noreferrer">https://kubernetes.io/zh/docs/tutorials/kubernetes-basics/</a></p><ul><li><strong>Apache MESOS</strong>\uFF1A\u5F00\u6E90\u7684\u5206\u5E03\u5F0F\u8D44\u6E90\u7BA1\u7406\u6846\u67B6 2019 \u5E74 5 \u6708\u4EFD Twitter \u8F6C\u5411 K8S</li><li><strong>Docker Swarm</strong>\uFF1A\u5BF9<code>docker</code>\u4E13\u95E8\u505A\u7684 2019 \u5E74 7 \u6708\u4EFD \u963F\u91CC\u4E91\u5BA3\u5E03\u4ECE\u963F\u91CC\u4E91\u7684\u9009\u62E9\u5217\u8868\u91CC\u5254\u9664</li><li><strong>Kubernetes</strong>\uFF1AGoogle 10 \u5E74\u524D\u5C31\u5F00\u59CB\u5BB9\u5668\u5316\u57FA\u7840\u67B6\u6784 \u7EC4\u4EF6\uFF1A<code>borg</code>\u7CFB\u7EDF\uFF0Cgoogle \u5185\u90E8\u7684 \u540E\u6765\u91C7\u7528\u4E86<code>Go</code>\u8BED\u8A00\u4F7F\u7528<code>borg</code>\u7684\u8BBE\u8BA1\u601D\u8DEF\u5F00\u53D1\u4E86 K8S \u5E76\u4E14\u5F00\u6E90\u7ED9\u4E86\u5BB9\u5668\u57FA\u91D1\u4F1A</li></ul><h3 id="\u7279\u70B9\uFF1A" tabindex="-1">\u7279\u70B9\uFF1A <a class="header-anchor" href="#\u7279\u70B9\uFF1A" aria-hidden="true">#</a></h3><ul><li>\u8F7B\u91CF\u7EA7\uFF1A\u6D88\u8017\u7684\u8D44\u6E90\u5C0F</li><li>\u5F00\u6E90</li><li>\u5F39\u6027\u4F38\u7F29</li><li>\u8D1F\u8F7D\u5747\u8861\uFF1ALVS</li></ul><h2 id="\u529F\u80FD" tabindex="-1">\u529F\u80FD <a class="header-anchor" href="#\u529F\u80FD" aria-hidden="true">#</a></h2><ol><li><p>\u81EA\u52A8\u88C5\u7BB1</p><blockquote><p>\u57FA\u4E8E\u5BB9\u5668\u5BF9\u5E94\u8FD0\u884C\u73AF\u5883\u7684\u8D44\u6E90\u914D\u7F6E\u8981\u6C42\u81EA\u52A8\u90E8\u7F72\u5E94\u7528\u5BB9\u5668</p></blockquote></li><li><p>\u81EA\u52A8\u4FEE\u590D(\u81EA\u6108\u80FD\u529B)</p><blockquote><p>\u5F53\u5BB9\u5668\u5931\u8D25\u65F6\uFF0C\u4F1A\u5BF9\u5BB9\u5668\u8FDB\u884C\u91CD\u542F</p><p>\u5F53\u6240\u90E8\u7F72\u7684\u8282\u70B9\u6709\u95EE\u9898\u65F6\uFF0C\u4F1A\u5BF9\u5BB9\u5668\u8FDB\u884C\u91CD\u65B0\u90E8\u7F72\u548C\u91CD\u65B0\u8C03\u5EA6</p><p>\u5F53\u5BB9\u5668\u672A\u901A\u8FC7\u76D1\u63A7\u68C0\u67E5\u65F6\uFF0C\u4F1A\u5173\u95ED\u6B64\u5BB9\u5668\u76F4\u5230\u5BB9\u5668\u6B63\u5E38\u8FD0\u884C\u65F6\uFF0C\u624D\u4F1A\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1</p></blockquote></li><li><p>\u6C34\u5E73\u6269\u5C55</p><blockquote><p>\u901A\u8FC7\u7B80\u5355\u7684\u547D\u4EE4\u3001\u7528\u6237 UI \u754C\u9762\u6216\u57FA\u4E8E CPU \u7B49\u8D44\u6E90\u4F7F\u7528\u60C5\u51B5\uFF0C\u5BF9\u5E94\u7528\u5BB9\u5668\u8FDB\u884C\u89C4\u6A21\u6269\u5927\u6216\u89C4\u6A21\u526A\u88C1\uFF1B</p><p>\u6BD4\u5982\uFF1A\u53CC\u5341\u4E00\u65F6\u4F1A\u6709\u5927\u91CF\u8BF7\u6C42\uFF0C\u4F1A\u8FDB\u884C\u6269\u5927\uFF1B\u7B49\u5230\u6D3B\u52A8\u7ED3\u675F\u4E4B\u540E\u4F1A\u88C1\u526A</p></blockquote></li><li><p>\u670D\u52A1\u53D1\u73B0</p><blockquote><p>\u7528\u6237\u4E0D\u9700\u8981\u4F7F\u7528\u989D\u5916\u7684\u670D\u52A1\u53D1\u73B0\u673A\u5236\uFF0C\u5C31\u80FD\u591F\u57FA\u4E8E K8S \u81EA\u8EAB\u80FD\u529B\u5B9E\u73B0\u670D\u52A1\u53D1\u73B0\u548C\u8D1F\u8F7D\u5747\u8861\uFF1B\u5BF9\u5916\u670D\u52A1\u6709\u4E00\u4E2A\u7EDF\u4E00\u7684\u5165\u53E3\uFF1A<code>Service</code>\uFF0C\u5982\u679C\u8BBF\u95EE\u8BA2\u5355\u670D\u52A1\u5C31\u53EA\u8BBF\u95EE\u8BA2\u5355\u7684\u8282\u70B9\u7B49\uFF0C\u8FD8\u53EF\u4EE5\u5728\u8FD9\u4E24\u8005\u4E4B\u95F4\u505A\u8D1F\u8F7D\u5747\u8861</p></blockquote></li><li><p>\u6EDA\u52A8\u66F4\u65B0</p><blockquote><p>\u53EF\u4EE5\u6839\u636E\u5E94\u7528\u7684\u53D8\u5316\uFF0C\u5BF9\u5E94\u7528\u5BB9\u5668\u8FD0\u884C\u7684\u5E94\u7528\uFF0C\u8FDB\u884C\u4E00\u6B21\u6027\u6216\u6279\u91CF\u5F0F\u66F4\u65B0\uFF1B\u5BF9\u52A0\u8FDB\u53BB\u7684\u670D\u52A1\u4F1A\u8FDB\u884C\u68C0\u6D4B\uFF0C\u5982\u679C\u5E94\u7528\u6CA1\u6709\u95EE\u9898\u7684\u65F6\u5019\uFF0C\u624D\u4F1A\u5F00\u59CB\u5BF9\u5916\u8FDB\u884C\u670D\u52A1\u3002</p></blockquote></li><li><p>\u7248\u672C\u56DE\u9000</p><blockquote><p>\u53EF\u4EE5\u6839\u636E\u5E94\u7528\u90E8\u7F72\u60C5\u51B5\uFF0C\u5BF9\u5E94\u5E94\u7528\u5BB9\u5668\u8FD0\u884C\u7684\u5E94\u7528\uFF0C\u8FDB\u884C\u5386\u53F2\u7248\u672C\u5373\u65F6\u56DE\u9000</p></blockquote></li><li><p>\u79D8\u94A5\u548C\u914D\u7F6E\u7BA1\u7406</p><blockquote><p>\u5728\u4E0D\u9700\u8981\u91CD\u65B0\u6784\u5EFA\u955C\u50CF\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EF\u4EE5\u90E8\u7F72\u548C\u66F4\u65B0\u79D8\u94A5\u548C\u5E94\u7528\u914D\u7F6E\uFF0C\u7C7B\u4F3C\u70ED\u90E8\u7F72</p></blockquote></li><li><p>\u5B58\u50A8\u7F16\u6392</p><blockquote><p>\u81EA\u52A8\u5B9E\u73B0\u5B58\u50A8\u7CFB\u7EDF\u6302\u8F7D\u53CA\u5E94\u7528\uFF0C\u7279\u522B\u5BF9\u6709\u72B6\u6001\u5E94\u7528\u5B9E\u73B0\u6570\u636E\u6301\u4E45\u5316\u975E\u5E38\u91CD\u8981\uFF0C\u5B58\u50A8\u7CFB\u7EDF\u53EF\u4EE5\u6765\u81EA\u4E8E\u672C\u5730\u76EE\u5F55\u3001\u7F51\u7EDC\u5B58\u50A8(NFS\u3001Gluster\u3001Ceph \u7B49)\u3001\u516C\u5171\u4E91\u5B58\u50A8\u670D\u52A1</p></blockquote></li><li><p>\u6279\u5904\u7406</p><blockquote><p>\u63D0\u4F9B\u4E00\u6B21\u6027\u4EFB\u52A1\u3001\u5B9A\u65F6\u4EFB\u52A1\uFF1B\u6EE1\u8DB3\u6279\u91CF\u6570\u636E\u5904\u7406\u548C\u5206\u6790\u7684\u573A\u666F</p></blockquote></li></ol>',8),r=[c];function p(s,a,d,n,u,k){return t(),o("div",null,r)}const _=e(i,[["render",p]]);export{h as __pageData,_ as default};
